import React, { useState, useEffect } from 'react';
import './ImageSlider.css'; // Make sure to adjust the path as necessary

const ImageSlider = () => {
  const [index, setIndex] = useState(0);
  const slides = [
    {
      image: '../assets/sliderbackground.jpeg',
      active: true,
    },
    {
      image: './assets/imagesliderbackground2.png',
      active: false,
    },
    // Add more slides as needed
  ];

  const totalSlides = slides.length;

  const showSlide = (i) => {
    const newIndex = (index + i + totalSlides) % totalSlides;
    setIndex(newIndex);
  };

  useEffect(() => {
    const interval = setInterval(() => {
      showSlide(1);
    }, 3000);

    return () => clearInterval(interval);
  }, [index]);

  return (
    <div className="slider">
      <div className="slides">
        {slides.map((slide, slideIndex) => (
          <div
            key={slideIndex}
            className={`slide ${slideIndex === index ? 'active' : ''}`}
          >
            <img src={slide.image} alt={`Slide ${slideIndex + 1}`} />
            {/* Add additional slide content as needed */}
          </div>
        ))}
      </div>
      <button className="arrow left" onClick={() => showSlide(-1)}>
        ‹
      </button>
      <button className="arrow right" onClick={() => showSlide(1)}>
        ›
      </button>
    </div>
  );
};

export default ImageSlider;
