import React,{useRef, use} from 'react';
import './Services.css';
import Card_1 from '../assets/Card_1.jpg'
import useIntersectionObserver from '../../hooks/useIntersectionObserver';

const Services = () => {
  const elementsRef = useRef([]);

  const handleIntersect = (entries) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        entry.target.classList.add('animate');
      }
    });
  };

  const observe = useIntersectionObserver(handleIntersect, {
    threshold: 0.1,
  });

  useEffect(() => {
    elementsRef.current.forEach((element) => {
      observe(element);
    });
  }, [observe]);
  return (
    <div className='Services'>
      <div className='hd'>
        <h1>Services</h1>
        <p>All my services are categorized into these 2 subsets - select from the below to explore further</p>
      </div>
      <div className='Cards'>
      <div className="Card1" style={{ height: "494px",width: "465px" }} ref={(el) => elementsRef.current[0] = el}>
          <div className="crd">
            <div className="cnt">
              <h1>One on One Life Coaching</h1>
              <p>
                The sessions are customised to cater your needs that will help you get
                clarity in your thoughts, courage in your decisions and bring consistency
                in your life.
              </p>
              <a href="#" className="learn-more">
                learn more →
              </a>
            </div>
          </div>
        </div>
        <div className="Card2" style={{height: "494px",width: "465px"}} ref={(el) => elementsRef.current[0] = el}>
          <div className="crd">
            <div className="cnt">
              <h1>One on One Life Coaching</h1>
              <p>
                The sessions are customised to cater your needs that will help you get
                clarity in your thoughts, courage in your decisions and bring consistency
                in your life.
              </p>
              <a href="#" className="learn-more">
                learn more →
              </a>
            </div>
          </div>
        </div>
      </div>
      <div>

      </div>
    </div>
  )
}

export default Services
