import React, { useState } from 'react';
import './Navbar.css';
import logoo from '../assets/logo.png';
const Navbar = ({ scrollToSection, aboutRef, servicesRef, blogRef, resourceRef }) => {
  const [menu, setMenu] = useState("");

  const handleScrollToSection = (ref) => {
    const offsetTop = ref.current.offsetTop - 70; // Assuming 70px is the height of your fixed navbar
    window.scrollTo({
      top: offsetTop,
      behavior: 'smooth'
    });
  };

  return (
    <div className='navbar'>
      <div className='nav-logo'>
        <img src={logoo} alt="logo" />
        {/* <p>Tanuja Devang</p> */}
      </div>

      <div className='nav-menu-container'>
        <ul className="nav-menu">
          <li onClick={() => { setMenu("about"); handleScrollToSection(aboutRef); }} className={`nav-item ${menu === "about" ? 'active' : ''}`}>
            About
          </li>
          <li onClick={() => { setMenu("services"); handleScrollToSection(servicesRef); }} className={`nav-item ${menu === "services" ? 'active' : ''}`}>
            Services
          </li>
          <li onClick={() => { setMenu("blog"); handleScrollToSection(blogRef); }} className={`nav-item ${menu === "blog" ? 'active' : ''}`}>
            Blog
          </li>
          <li onClick={() => { setMenu("instagram"); }} className={`nav-item ${menu === "instagram" ? 'active' : ''}`}>
            Instagram
          </li>
          <li onClick={() => { setMenu("resources"); handleScrollToSection(resourceRef); }} className={`nav-item ${menu === "resources" ? 'active' : ''}`}>
            Resources
          </li>
        </ul>
      </div>
    </div>
  );
};
export default Ns
