import React, { useState, useEffect } from 'react';
import './ImageSlider.css';

const ImageSlider = () => {
  const [index, setIndex] = useState(0);
  const slides = [
    {
      image: '../assets/sliderbackground.jpeg',
      title: 'Taking care',
      subtitle: 'of your life issues',
      content:
        "We all know how life can be challenging and hard. And that there’s no answer always to all the issues you may face. I’m here to help you.",
      link1: '#',
      link2: '#',
    },
    {
      image: '../assets/imagesliderbackground2.png',
      title: "Celeste's Coaching",
      subtitle: 'transform lives',
      content:
        "We all know how life can be challenging and hard. And that there’s no answer always to all the issues you may face. I’m here to help you.",
      link1: '#',
      link2: '#',
    },
    // Add more slide objects as needed
  ];

  const totalSlides = slides.length;

  const showSlide = (i) => {
    setIndex((prevIndex) => (prevIndex + i + totalSlides) % totalSlides);
  };

  useEffect(() => {
    const interval = setInterval(() => {
      showSlide(1);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="slider">
      <div className="slides">
        {slides.map((slide, slideIndex) => (
          <div
            key={slideIndex}
            className={`slide ${index === slideIndex ? 'active' : ''}`}
          >
            <img src={slide.image} alt={`Slide ${slideIndex + 1}`} />
            <div className={`slide-${slideIndex + 1}`}>
              <div className="slide-content">
                <h1>{slide.title}</h1>
                <h2>{slide.subtitle}</h2>
                <p>{slide.content}</p>
                <a href={slide.link1} className="fombtn">
                  FIND OUT MORE
                </a>
                <a href={slide.link2} className="ppbtn">
                  PRICING & PACKAGES
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>
      <button className="arrow left" onClick={() => showSlide(-1)}>
        ‹
      </button>
      <button className="arrow right" onClick={() => showSlide(1)}>
        ›
      </button>
    </div>
  );
};

export default ImageSlider;
